              elif test -n "${neighbor-interface}"; then

                 python $SONIC_CLI_ROOT/sonic_cli_bgp.py get_ip_bgp_neighbors_neighborip show_ip_bgp_neighbors.j2 ${vrf-name} $iptype ${Ethernet} ${PortChannel} ${Vlan} ${__full_line}

	      else

                 python $SONIC_CLI_ROOT/sonic_cli_bgp.py get_ip_bgp_neighbors show_ip_bgp_neighbors.j2 ${vrf-name} $iptype ${__full_line}

              fi

           fi

        else

           python $SONIC_CLI_ROOT/sonic_cli_bgp.py get_show_bgp ${__full_line}

        fi

